      if (errors.length > 0) {
        return res.json({ valid: false, conflict: conflicts })
      }
      return res.json({ valid: true })
    });

  app.route('/api/solve')
    .post((req, res) => {
      console.log(req.body)
      if ( !req.body.puzzle ) {
